import React from 'react';
import { TrendingUp, TrendingDown } from 'lucide-react';

interface CreditScoreCardProps {
  score: number;
  previousScore?: number;
}

const CreditScoreCard: React.FC<CreditScoreCardProps> = ({ 
  score, 
  previousScore = score - 12 
}) => {
  const getScoreColor = (score: number) => {
    if (score >= 750) return 'text-success-600';
    if (score >= 700) return 'text-warning-600';
    return 'text-danger-600';
  };

  const getScoreRing = (score: number) => {
    if (score >= 750) return 'stroke-success-600';
    if (score >= 700) return 'stroke-warning-600';
    return 'stroke-danger-600';
  };

  const getScoreRange = (score: number) => {
    if (score >= 750) return 'Excellent';
    if (score >= 700) return 'Good';
    if (score >= 650) return 'Fair';
    return 'Poor';
  };

  const percentage = (score / 850) * 100;
  const circumference = 2 * Math.PI * 45;
  const strokeDashoffset = circumference - (percentage / 100) * circumference;
  
  const scoreDifference = score - previousScore;
  const isPositive = scoreDifference > 0;

  return (
    <div className="card p-6">
      <div className="text-center">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">
          Credit Score
        </h3>
        
        <div className="relative w-32 h-32 mx-auto mb-4">
          <svg className="w-32 h-32 transform -rotate-90" viewBox="0 0 100 100">
            {/* Background circle */}
            <circle
              cx="50"
              cy="50"
              r="45"
              stroke="currentColor"
              strokeWidth="8"
              fill="none"
              className="text-gray-200"
            />
            {/* Progress circle */}
            <circle
              cx="50"
              cy="50"
              r="45"
              stroke="currentColor"
              strokeWidth="8"
              fill="none"
              strokeDasharray={circumference}
              strokeDashoffset={strokeDashoffset}
              className={`transition-all duration-1000 ease-out ${getScoreRing(score)}`}
              strokeLinecap="round"
            />
          </svg>
          
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <div className={`text-3xl font-bold ${getScoreColor(score)}`}>
                {score}
              </div>
              <div className="text-xs text-gray-500">out of 850</div>
            </div>
          </div>
        </div>
        
        <div className="mb-4">
          <div className={`text-sm font-medium ${getScoreColor(score)}`}>
            {getScoreRange(score)}
          </div>
        </div>
        
        <div className="flex items-center justify-center space-x-2">
          {isPositive ? (
            <TrendingUp className="h-4 w-4 text-success-600" />
          ) : (
            <TrendingDown className="h-4 w-4 text-danger-600" />
          )}
          <span className={`text-sm font-medium ${
            isPositive ? 'text-success-600' : 'text-danger-600'
          }`}>
            {isPositive ? '+' : ''}{scoreDifference} points
          </span>
          <span className="text-sm text-gray-500">vs last month</span>
        </div>
      </div>
    </div>
  );
};

export default CreditScoreCard;
